import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUser, removeUserSession, getToken } from '../utils/common';
import axios from 'axios';

const Dashboard = props => {
  const history = useNavigate();
  const user = getUser();
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [transactions, setTransaction] = useState([]);

  useEffect(() => {
    const token = getToken();
    if (!token) {
      return;
    }

  });

  // handle click event of logout button
  const handleLogout = () => {
    removeUserSession();
    history('/login');
  }

  const allTransactions = () => {
    const token = getToken();
    if (!token) {
      return;
    }
    axios.post('https://jp-dev.cityremit.global/web-api/transaction-manager/v1/admin/dashboard/search', {},
      {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
      }).then(response => {
        setTransaction(response.data["data"])
      }).catch(error => {
        setLoading(false);
        if (error.response?.status === 403) setError(error.response.data.message);
        else setError("Something went wrong. Please try again later.");
      });
  }

  const listItems = transactions.map((data, index) => {
    console.log(data, "ddd")
    return (
      <li key={data["id"]}>{data["id"]}</li>

    )
  }
  );
  return (
    <div>
      Welcome {user}!<br /><br />
      <button type="button" onClick={allTransactions}>Click Me!</button>
      <input type="button" onClick={handleLogout} value="Logout" />
      <br />
      <br />
      <div >
        {
          transactions.length > 0 ? {
            listItems
          } : <></>
        }
      </div>
    </div >
  );
}

export default Dashboard;
