import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUser, removeUserSession, getToken } from '../utils/common';
import axios from 'axios';

const Dashboard = props => {
  const history = useNavigate();
  const user = getUser();
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const token = getToken();
    if (!token) {
      return;
    }
    console.log(token, "ghtoke")
    axios.post('https://jp-dev.cityremit.global/web-api/transaction-manager/v1/admin/dashboard/search',
     {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
    },
    }).then(response => {
      console.log(response, "responTest")
      // setLoading(false);
      // setUserSession(response.data["data"][0]["jwt_token"], response.data["data"][0]["login_id"]);
      // history('/dashboard');
    }).catch(error => {
      setLoading(false);
      if (error.response?.status === 403) setError(error.response.data.message);
      else setError("Something went wrong. Please try again later.");
    });


  });

// handle click event of logout button
const handleLogout = () => {
  removeUserSession();
  history('/login');
}



return (
  <div>
    Welcome {user}!<br /><br />
    <input type="button" onClick={handleLogout} value="Logout" />
  </div>
);
}

export default Dashboard;
