import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUser, removeUserSession, getToken } from '../utils/common';
import axios from 'axios';

const Dashboard = props => {
  const history = useNavigate();
  const user = getUser();
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [transactions, setTransaction] = useState([]);

  useEffect(() => {
    const token = getToken();
    if (!token) {
      return;
    }

    axios.post('https://jp-dev.cityremit.global/web-api/transaction-manager/v1/admin/dashboard/search', {},
    {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    }).then(response => {
      setTransaction(response.data["data"])
    }).catch(error => {
      setLoading(false);
      if (error.response?.status === 403) setError(error.response.data.message);
      else setError("Something went wrong. Please try again later.");
    });
}
  );

  // handle click event of logout button
  const handleLogout = () => {
    removeUserSession();
    history('/login');
  }

  return (
    <div>
      Welcome {user}!<br /><br />
      <input type="button" onClick={handleLogout} value="Logout" />
      <br />
      <br />
      <div >
        {
          transactions.length > 0 ?
            <table>
              <tr>
                <th>Current Status</th>
                <th>Receive Amount
                </th>
                <th>Receive Country
                </th>
                <th>Receiver Full Name</th>
                <th>Send Amount
                </th>
                <th>Send Country
                </th>
                <th>Sender Full Name</th>
              </tr>
              {
                transactions.map((data, index) => {
                  return (
                    <>
                      <tr key={index}>
                        <td>{data["Current Status"]}</td>
                        <td>{data["Receive Amount/受取金額"]}</td>
                        <td>{data["Receive Country/受取国"]}</td>
                        <td>{data["Receiver Full Name"]}</td>
                        <td>{data["Send Amount/送金額"]}</td>
                        <td>{data["Send Country/送金国"]}</td>
                        <td>{data["Sender Full Name"]}</td>
                      </tr>
                    </>
                  )
                })
              }
            </table>
            : <></>
        }
      </div>
    </div >
  );
}

export default Dashboard;
